mapper.select:
select * from ( select rownum r, BM.* from ( select B.board_no, b.board_content, b.board_state, to_char(b.board_wdate, 'yyyy-mm-dd') board_wdate, b.board_writer, b.member_no, b.photo, M.email from board B inner join member M on B.member_no = M.member_no order by board_no) BM where ${type} like '%'||#{keyword}||'%'
and photo >= 0
and photo >= 1
and board_state = #{board_state}
and board_content like '%'||#{board_content}||'%'
and board_wdate = #{board_wdate}
) where r between #{start} and #{finish}

select count(*) from ( select rownum r, BM.* from ( select B.board_no, b.board_content, b.board_state, to_char(b.board_wdate, 'yyyy-mm-dd') board_wdate, b.board_writer, b.member_no, b.photo, M.email from board B inner join member M on B.member_no = M.member_no order by board_no) BM where ${type} like '%'||#{keyword}||'%'
and photo >= 0
and photo >= 1
and board_state = #{board_state}
and board_content like '%'||#{board_content}||'%'
and board_wdate = #{board_wdate}
)

select * from(
	select rownum r, MP.* from(
		select M.*, to_char(sysdate,'yyyy')-substr(M.member_birth,1,4)+1 age from member_member_profile M order by member_no desc) MP
		where ${type} like '%'||#{keyword}||'%'
		
		and member_state=#{member_state}
		
		
		and member_gender=#{member_gender}
		
		
		and member_phone=#{member_phone}			
		
		
		and age=#{member_birth}
				
)
where r between #{start} and #{finish}

select * from(
	select rownum r, MP.* from(
		select M.*, to_char(sysdate,'yyyy')-substr(M.member_birth,1,4)+1 age from member_member_profile M order by member_no desc) MP
		where ${type} like '%'||#{keyword}||'%'
		
		and member_state=#{member_state}
		
		
		and member_gender=#{member_gender}
		
		
		and member_phone=#{member_phone}			
		
		
		and age=#{member_birth}
				
)
where r between #{start} and #{finish}

select count(*) from(
	select rownum r, MP.* from(
		select M.*, to_char(sysdate,'yyyy')-substr(M.member_birth,1,4)+1 age from member_member_profile M order by member_no desc) MP
		where ${type} like '%'||#{keyword}||'%'
		
		and member_state=#{member_state}
		
		
		and member_gender=#{member_gender}
		
		
		and member_phone=#{member_phone}			
		
		
		and age=#{member_birth}
				
)

select * from( select rownum r, WL.* from( select w.warning_no, tm.email target_email, tm.member_name target_name, w.target_no, wc.w_count, pm.email pusher_email, pm.member_name pusher_name, w.pusher_no, w.board_no, w.content, w.state from warning W inner join member TM on W.target_no = TM.member_no inner join member PM on W.pusher_no = PM.member_no left outer join warning_count WC on W.target_no = WC.member_no order by w.warning_no desc) WL where ${target_type} like '%'||#{target_keyword}||'%' and ${pusher_type} like '%'||#{pusher_keyword}||'%'
and content like '%'||#{content_keyword}||'%'
and state is null and state = '접수' and state = '보류' ) where r between #{start} and #{finish}
